code_pattern_examples.sentiment_analysis:
// This simple code checks if your expensive AI sentiment analysis
// is actually better than a coin flip
function realityCheckSentiment(aiPredictions, actualSentiments) {
  let accuracy = aiPredictions.filter((pred, i) => pred === actualSentiments[i]).length / aiPredictions.length;
  return `Your $50K sentiment model is ${(accuracy * 100).toFixed(1)}% accurate, which is ${(accuracy * 100 - 50).toFixed(1)}% better than guessing`;
}

code_pattern_examples.jargon_detector:
// Quantify the buzzword density in your corporate communications
function buzzwordDensity(text, buzzwords) {
  const words = text.toLowerCase().split(/\W+/);
  const buzzCount = words.filter(word => buzzwords.includes(word)).length;
  return `Your communication is ${((buzzCount / words.length) * 100).toFixed(1)}% buzzwords. Try using real words instead.`;
}
